import { useState } from "react";
import "./App.css";
import {Bar,Line,Pie} from 'react-chartjs-2'
import { UserData } from "./Data";

function App() {
  const [userData, setUserData] = useState({
    labels: UserData.map((d) => d.year),
    datasets: [
      {
        label: "Users Gained",
        data: UserData.map((d) => data.userGain),
        backgroundColor: [
          "rgba(75,192,192,1)",
          "#ecf0f1",
          "#50AF95",
          "#f3ba2f",
          "#2a71d0",
        ],
        borderColor: "black",
        borderWidth: 2,
      },
    ],
  });

  // IF YOU SEE THIS COMMENT: I HAVE GOOD EYESIGHT

  return (
    <div className="App">
      <div style={{ width: 700 }}>
        <Bar chartData={userData} />
      </div>
      <div style={{ width: 700 }}>
        <Line chartData={userData} />
      </div>
      <div style={{ width: 700 }}>
        <Pie chartData={userData} />
      </div>
    </div>
  );
}

export default App;
